import React, { useState } from "react";
import { useParams } from "react-router-dom";

export default function HouseDetails(props) {
  const { houseId } = useParams(); // Assuming you're using React Router to get the houseId
  const [selectedHouse, setSelectedHouse] = useState(null);

  // Find the selected house based on the houseId
  const selectedHouseData = props.houses.find((house) => house.Home_id === Number(houseId));

  // Handle click event to display the selected house
  const handleHouseClick = () => {
    setSelectedHouse(selectedHouseData);
  };

  // Styling using CSS classes
  const containerStyle = {
    fontFamily: "Arial, sans-serif",
    maxWidth: "400px",
    margin: "0 auto",
    padding: "20px",
  backgroundColor: "#f7f7f7",
  };

  const houseDetailStyle = {
    fontSize: "18px",
    margin: "10px 0",
  };

  return (
    <div style={containerStyle}>
      <h2>House Details</h2>
      {selectedHouse ? (
        <div>
          <p style={houseDetailStyle}>Home ID: {selectedHouse.Home_id}</p>
          <p style={houseDetailStyle}>City: {selectedHouse.Home_City}</p>
          <p style={houseDetailStyle}>Governorate: {selectedHouse.Home_Governorate}</p>
          <p style={houseDetailStyle}>Nature: {selectedHouse.Home_Nature}</p>
          <p style={houseDetailStyle}>Type: {selectedHouse.Home_Type}</p>
        </div>
      ) : (
        <p style={{ ...houseDetailStyle, fontStyle: "italic" }}>
          Click on a house to view its details.
        </p>
      )}

      <button onClick={handleHouseClick} style={styles.button}>
        View House
      </button>
    </div>
  );
}

const styles = {
  button: {
    backgroundColor: "blue",
    color: "white",
    padding: "10px 20px",
    fontSize: "16px",
    borderRadius: "5px",
    cursor: "pointer",
    marginTop: "10px",
  },
};
