import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  MDBCard,
  MDBCardBody,
  MDBCardText,
  MDBCardImage
} from 'mdb-react-ui-kit';

export default function ListHouses() {
  const [data, setData] = useState([]);
  const [images ,setImages] =useState([])
  console.log(images)
  const getData = () => {
    axios.get("http://localhost:3000/api/home/getAll")
      .then((response) => {
        setData(response.data);
      });
  }
  const getImages =()=>{
   axios.get(`http://localhost:3000/api/home/getImageById`)
      .then((response)=>{
        setImages(response.data)
      
      })
  }
  const getOneImageSrc =(id)=>{
    for(var i=0 ; i<images.length ;i++){
      if(images[i]["Home_Home_id"]===id){
        console.log(images[i]["HomeImages_data"].data[0])
        return images[i]["HomeImages_data"]
      }
    }
  }

  useEffect(() => {
    getData();
    getImages();
    // console.log("this is the data " ,data)
    // console.log("this is the images " ,images)
  }, []);
  

  return (
    <div>
      <h2 style={{ color: '#3b71ca', marginLeft: "15px" }}>available real estates</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {data.map((house, index) => (
          <MDBCard key={index} style={{ width: '300px', margin: '10px' }}>
            <MDBCardImage src={getOneImageSrc(house.Home_id)} alt='...' position='top' />
            <MDBCardBody>
              <MDBCardText>
                {house.Home_City}
                  <br/>                
                 {house.Home_Governorate}
              </MDBCardText>
            </MDBCardBody>
          </MDBCard>
        ))}
      </div>
    </div>
  );
}
