import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  MDBCard,
  MDBCardBody,
  MDBCardText,
  MDBCardImage
} from 'mdb-react-ui-kit';
import { Link } from "react-router-dom"
import HouseDetails from './HouseDetails';

export default function ListHouses() {
  const [data, setData] = useState([]);
  const [images, setImages] = useState([]);
  const [view, setView] = useState("ListHouses");

  const getData = () => {
    axios.get("http://localhost:3000/api/home/getAll")
      .then((response) => {
        setData(response.data);
      });
  }

  const getImages = () => {
    axios.get(`http://localhost:3000/api/home/getImageById`)
      .then((response) => {
        setImages(response.data);
      })
  }

  const getOneImageSrc = (id) => {
    for (var i = 0; i < images.length; i++) {
      if (images[i]["Home_Home_id"] === id) {
        const url = String.fromCharCode(...(images[i]["HomeImages_data"].data));
        return url;
      }
    }
  }

  useEffect(() => {
    getData();
    getImages();
  }, []);

  return (
    <div>
      <h2 style={{ color: '#3b71ca', marginLeft: "15px" }}>available real estates</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {data.map((house, index) => (
          <MDBCard key={index} style={{ width: '300px', margin: '10px' }}>
            <div>
              <Link to={`/houseDetails/${house.Home_id}`}>
                <MDBCardImage src={getOneImageSrc(house.Home_id)} alt='...' position='top' />
                <HouseDetails data={data} images={images} />
              </Link>
            </div>
            <MDBCardBody>
              <MDBCardText>
                {house.Home_City}
                <br />
                {house.Home_Governorate}
              </MDBCardText>
            </MDBCardBody>
          </MDBCard>
        ))}
      </div>
    </div>
  );
}
