import React, { useState } from 'react';
import {
  MDBValidation,
  MDBValidationItem,
  MDBInput,
  MDBInputGroup,
  MDBBtn,
  MDBCheckbox
} from 'mdb-react-ui-kit';

export default function Search() {
  const [formValue, setFormValue] = useState({
    Nature: '',
    governorate: '',
    city: '',
    type: '',
  });

  const onChange = (e) => {
    setFormValue({ ...formValue, [e.target.name]: e.target.value });
  };
  return (
    <MDBValidation className='row g-3' style={{marginLeft:"15px"}} onSubmit={()=>{console.log()}}>
      <MDBValidationItem className='col-md-6' feedback='Please provide a valid city.' invalid  style={{ width: '300px'}}>
        <MDBInput
          value={formValue.city}
          name='city'
          onChange={onChange}
          id='validationCustom03'
          label='City'
        />
      </MDBValidationItem>
      <MDBValidationItem className='col-md-4' style={{ width: '300px' }}>
        <MDBInput
        style={{width : "150px"}}
          value={formValue.governorate}
          name='governorate'
          onChange={onChange}
          id='validationCustom02'
          label='governorate'
        />
      </MDBValidationItem>
      <MDBValidationItem feedback='Please choose a Nature.' invalid className='col-md-4' style={{ width: '300px',  }}>
        
          <MDBInput
            type='text'
            value={formValue.Nature}
            name='Nature'
            onChange={onChange}
            label='Nature'
            className='form-control'
            id='validationCustomUsername'
            placeholder='Nature'
          />
     
      </MDBValidationItem>

      <MDBValidationItem className='col-md-6' feedback='Please provide a valid type.' invalid style={{ width: '300px' }}>
        <MDBInput
          value={formValue.type}
          name='type'
          onChange={onChange}
          id='validationCustom05'
          label='type'
        />
      </MDBValidationItem>

      <div className='col-12'>
        <MDBBtn type='submit'>Search</MDBBtn>

      </div>
    </MDBValidation>
  );
}
