import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  MDBCard,
  MDBCardBody,
  MDBCardText,
  MDBCardImage
} from 'mdb-react-ui-kit';

export default function ListHouses() {
  const [data, setData] = useState([]);
  const [images ,setImages] =useState([])
  console.log(images)
  const getData = () => {
    axios.get("http://localhost:3000/api/home/getAll")
      .then((response) => {
        setData(response.data);
        const images =response.data.map((house)=>{
          axios.get(`http://localhost:3000/api/home/getImageById`)
            .then((response)=>{
              setImages({'url':response.data, 'houseId' :house.Home_id })
            
            })
        })
      });
  }
  const getOneImageSrc =(id)=>{
    for(var i=0 ; i<images.length ;i++){
      if(images[i]["houseId"]===id){
        return images[i]["url"]
      }
    }
  }

  useEffect(() => {
    getData();
  }, []);
  

  return (
    <div>
      <h2 style={{ color: '#3b71ca', marginLeft: "15px" }}>available real estates</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {data.map((house, index) => (
          <MDBCard key={index} style={{ width: '300px', margin: '10px' }}>
            {useEffect(()=>axios.get(`http://localhost:3000/api/home/getImageById/${house.Home_id}`)
            .then((response)=>{
              setImages({'url':response.data, 'houseId' :house.Home_id })
            
            }),[])
            }
            <MDBCardImage src={getOneImageSrc(house.Home_id)} alt='...' position='top' />
            <MDBCardBody>
              <MDBCardText>
                {house.Home_City}
                  <br/>                
                 {house.Home_Governorate}
              </MDBCardText>
            </MDBCardBody>
          </MDBCard>
        ))}
      </div>
    </div>
  );
}
