import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  MDBCard,
  MDBCardBody,
  MDBCardText,
  MDBCardImage
} from 'mdb-react-ui-kit';

export default function ListHouses() {
  const [data, setData] = useState([]);
  const [images, setImages] = useState([]);

  const getData = () => {
    axios.get("http://localhost:3000/api/home/getAll")
      .then((response) => {
        setData(response.data);

        // Fetch images for all houses and update the images state
        const imagePromises = response.data.map((house) => {
          return axios.get(`http://localhost:3000/api/home/getImageById/${house.Home_id}`)
            .then((imageResponse) => {
              return { url: imageResponse.data, houseId: house.Home_id };
            });
        });

        Promise.all(imagePromises).then((imageData) => {
          setImages(imageData);
        });
      });
  }

  const getOneImageSrc = (id) => {
    const image = images.find((img) => img.houseId === id);
    return image ? image.url : null;
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <div>
      <h2 style={{ color: '#3b71ca', marginLeft: "15px" }}>available real estates</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {data.map((house, index) => (
          <MDBCard key={index} style={{ width: '300px', margin: '10px' }}>
            <MDBCardImage src={getOneImageSrc(house.Home_id)} alt='...' position='top' />
            <MDBCardBody>
              <MDBCardText>
                {house.Home_City}
                <br/>
                {house.Home_Governorate}
              </MDBCardText>
            </MDBCardBody>
          </MDBCard>
        ))}
      </div>
    </div>
  );
}
