import React from "react";

export default function HouseDetails(props) {
  const filteredHouse = props.houses.find((house) => house.Home_id === Number(props.houseId));
  const filteredImages = props.images.filter((houseImage) => {
    return Number(props.houseId) === houseImage.Home_Home_id;
  });
  console.log("this is filtered image ", filteredImages);

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>House Details</h2>
      <div style={styles.imageContainer}>
        {filteredImages.map((image, index) => {
          const url = String.fromCharCode(...image["HomeImages_data"].data);
          return (
            <img
              key={index}
              src={url}
              alt="Image"
              style={styles.image}
            />
          );
        })}
      </div>
      {filteredHouse ? (
        <div>
          <div style={styles.detail}>
            <strong>Home ID:</strong> {filteredHouse.Home_id}
          </div>
          <div style={styles.detail}>
            <strong>City:</strong> {filteredHouse.Home_City}
          </div>
          <div style={styles.detail}>
            <strong>Governorate:</strong> {filteredHouse.Home_Governorate}
          </div>
          <div style={styles.detail}>
            <strong>Nature:</strong> {filteredHouse.Home_Nature}
          </div>
          <div style={styles.detail}>
            <strong>Type:</strong> {filteredHouse.Home_Type}
          </div>
        </div>
      ) : (
        <p style={styles.error}>No house found with the specified ID.</p>
      )}
    </div>
  );
}

const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    maxWidth: "400px",
    margin: "0 auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "white",
    zIndex: 1,
  },
  title: {
    fontSize: "24px",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  detail: {
    margin: "10px 0",
  },
  error: {
    color: "red",
    fontStyle: "italic",
    textAlign: "center",
  },
  imageContainer: {
    display: "flex",        // Display images in a row
    justifyContent: "center", // Center images horizontally
  },
  image: {
    maxWidth: "100%",       // Set maximum width to 100% of the container
    maxHeight: "100%",      // Set maximum height to 100% of the container
    objectFit: "cover",     // Maintain aspect ratio and cover the container
    marginBottom: "10px",
    marginLeft: "10px",     // Add some spacing between images
  }
};
